<?php/*Login System Modified from code available here:http://code.google.com/p/php-login-script/Sends out emails - called by various admin tasks and password reset.*/error_reporting (E_ERROR | 0);include 'constants.php';$headers =  'From: '.Email_Address.'' . "\r\n" .		    'Reply-To: '.Email_Address.'' . "\r\n" .    		'X-Mailer: PHP/' . phpversion();// Email Sent when a user requests to reset their passwordfunction Mail_Reset_Password($login, $code, $email) {	$subject = 'Password Request';		$message = '	Dear '.$login.',		Your password for the '.Site_Name.' has been requested.	To reset your password please follow the link below.	'.Script_URL.Script_Path.'forgotpassword.php?login='.$login.'&code='.$code.'	If you didnt request your password please delete this email.		Thanks 		'.Email_From.'';		mail($email, $subject, $message, $headers, '-f'.Email_Address);}// Email sent if the user resets the password with the password recovery toolfunction Mail_Reset_Password_Confirmation($login, $email) {	$subject = 'Password Reset';		$message = '	Dear '.$login.',		Your password for the '.Site_Name.' has been reset.	If you did not request that your password was reset please contact the administrator.		Thanks 		'.Email_From.'';			mail($email, $subject, $message, $headers);}// Email sent when a user signs up to the systemfunction User_Created($login, $email) {	$subject = 'Welcome to '.Site_Name;		$message = '	Dear '.$login.',	Thanks for signing up to the '.Site_Name.'.';		// if admin approval is true.		$message .= 'You can login with your login and password at '.Script_URL.Script_Path.'.';	$message .= '	Thanks 		'.Email_From.'';		mail($email, $subject, $message, $headers, '-f'.Email_Address);}// Email sent to client if the admin changes their statusfunction Status_Changed($login, $email, $status) {	$subject = 'Welcome to '.Site_Name;		$message = '	Dear '.$login.',		The administrator has changed your account status to '.$status.',	Should you have any enquiries please contact the administrator.			Thanks 		'.Email_From.'';		mail($email, $subject, $message, $headers, '-f'.Email_Address);}?>